def waysToMakeFair(A):
    n = len(A)
    odd = [0] * n
    even = [0] * n
    count = 0
    oddSum, evenSum = 0, 0
    for i in range(len(A)):
        if i % 2:
            oddSum += A[i]
        else:
            evenSum += A[i]
        odd[i] = oddSum
        even[i] = evenSum
    for i in range(len(A)):
        if i == 0:
            oddSum = even[n-1] - A[0]
            evenSum = odd[n-1]
            if oddSum == evenSum:
                count += 1
        else:
            evenSum = even[i-1] + odd[n-1] - odd[i]
            oddSum = odd[i-1] + even[n-1] - even[i]
            if oddSum == evenSum:
                count += 1
    return count

