import math
def solve(A):
    count = 0
    for i in A:
        if isPrime(i) == True:
            count += 1
    return ((2**count)-1) % ((10**9)+7)
    
def isPrime(n):
    i = 2
    if n <= 1:
        return False
    for i in range(2, int(math.sqrt(n))+1):
        if n % i == 0:
            return False
        i += 1
    return True

